import ExpenseGenerator from "./ExpenseGenerator";
import ExpenseFilter from "./ExpenseFilter";
import Comp from "../UI/Comp";
import "./ExpenseItem.css";
import React, { useState } from "react";

function ExpenseItem(props) {
  const [filterYear, setFilterYear] = useState("2020");
  const ExpenseFilterHandler = (selectedYear) => {
    setFilterYear(selectedYear);
  };

  const filteredExpenses = props.items.filter((expense) => {
    return expense.date.getFullYear().toString() === filterYear;
  });

  let expensesContent = <p className="no_expense">No Expenses In This Year.</p>;

  if (filteredExpenses.length > 0){
    expensesContent = filteredExpenses.length > 0 &&
    filteredExpenses.map((expense) => (
      <ExpenseGenerator
        key={expense.id}
        title={expense.title}
        amount={expense.amount}
        date={expense.date}
      />
    ));
  }

  return (
    <div>
      <Comp className="expenses_item">
        <ExpenseFilter
          selected={filterYear}
          onExpenseFilter={ExpenseFilterHandler}
        />
        {expensesContent}
      </Comp>
    </div>
  );
}

export default ExpenseItem;

